(function () {
    var $, colorSchemes, markers, name, namespace, theme, themes;
    $ = jQuery;
    namespace = 'highcharts-themes';
    themes = {};
    colorSchemes = {
        base: ['#dd7722', '#2288cc', '#dd3322', '#22aa99', '#bb4488', '#ddaa00', '#6655cc', '#99aa00'],
        pastel: ['#6ccac9', '#f8d347', '#57c8f2', '#886DB3']
    };

    markers = {
        base: {
            enabled: true,
            lineWidth: 1,
            radius: 2,
            fillColor: '#FFFFFF',
            lineColor: null,
            symbol: 'circle',
            states: {
                hover: {
                    enabled: false,
                    radius: 1,
                    lineWidth: 5
                }
            }
        },
        pastel: {
            enabled: true,
            lineWidth: 3,
            radius: 5,
            fillColor: null,
            lineColor: '#FFFFFF',
            symbol: 'circle',
            states: {
                hover: {
                    lineWidth: 5,
                    radius: 7
                }
            }
        }
    };

    themes.pastel = {
        colors: colorSchemes.pastel,
        loading: {
            labelStyle: {
                color: 'white'
            },
            style: {
                backgroundColor: 'gray'
            }
        },
        plotOptions: {
            line: {
                lineWidth: 3,
                marker: markers.pastel
            },
            spline: {
                marker: markers.pastel
            },
            bar: {
                pointWidth: 5
            },
            column: {
                pointWidth: 10
            }
        }
    };


    themes._defaults = {
        chart: {
            style: {
                fontFamily: 'Helvetica',
                fontWeight: 'normal'
            }
        },
        xAxis: {
            lineColor: '#ccc'
        },
        yAxis: {
            gridLineColor: '#e0e0e0'
        },
        credits: false,
        legend: {
            borderRadius: 0,
            borderWidth: 0,
            align: 'center',
            x: 15
        }
    };

    for (name in themes) {
        theme = themes[name];
        if (name !== '_defaults') {
            $.extend(true, theme, themes._defaults);
        }
    }

    $.fn[namespace] = Highcharts.themes = themes;

    Highcharts.setTheme = function (themeName) {
        var t;
        if (t = Highcharts.themes[themeName] != null) {
            return Highcharts.setOptions(t);
        } else {
            return console.warn("Found no such theme.");
        }
    };

}).call(this);


Highcharts.theme = Highcharts.themes['pastel'];
Highcharts.theme.lang = {
    printChart: "打印图表",
    downloadJPEG: "下载JPEG 图片",
    downloadPDF: "下载PDF文档",
    downloadPNG: "下载PNG 图片",
    downloadSVG: "下载SVG 矢量图",
    exportButtonTitle: "导出图片",
    loading: '数据加载中...'
};
// Apply the theme
Highcharts.setOptions(Highcharts.theme);